function palindrome(str) {
  let result = true;
  let arr1 = str.split(' ');
  let filterArr = [];
  for (let item of arr1) {
    filterArr.push(...item.split(''));
  }
  filterArr = filterArr.filter(item => item !== '.' && item!== ',' && item !== '_' && item!== '-' && item!== '(' && item!== ')');
  filterArr = filterArr.map(item => item.toLowerCase());
  const amount = Math.floor(filterArr.length/2);
  for (let i=0; i<amount; i++){
    if (filterArr[i] !== filterArr[filterArr.length-1-i]) {
      result = false;
    }
  }
  console.log(filterArr);
  return result;
}


